// Map display names used in content data to simple-icons slugs
const TECH_SLUG_MAP: Record<string, string> = {
  "React": "react",
  "React Native": "react",
  "Vue.js": "vuedotjs",
  "Vue 3": "vuedotjs",
  "Next.js": "nextdotjs",
  "TypeScript": "typescript",
  "JavaScript": "javascript",
  "Laravel": "laravel",
  "Laravel 11": "laravel",
  "Laravel 11/12": "laravel",
  "Laravel 12": "laravel",
  "PHP": "php",
  "PHP 8.2+": "php",
  "MySQL": "mysql",
  "PostgreSQL": "postgresql",
  "Redis": "redis",
  "Expo": "expo",
  "Electron": "electron",
  "WebRTC": "webrtc",
  "Tailwind CSS": "tailwindcss",
  "Vite": "vite",
  "Kotlin": "kotlin",
  "SQLite": "sqlite",
  "Linux": "linux",
  "jQuery": "jquery",
  "Vercel": "vercel",
  "DigitalOcean": "digitalocean",
  "GitHub Actions": "githubactions",
};

export function getTechIcon(name: string): TechIcon | null {
  const slug = TECH_SLUG_MAP[name];
  if (!slug) return null;
  const iconData = ICON_REGISTRY[slug];
  if (!iconData) return null;
  return { name, path: iconData.path, hex: iconData.hex };
}
